.card {
   width: 100%;
   height: 100%;
   box-sizing: border-box;
   font-size: 160%;
   color: white;

   padding: 10px;
   display: flex;
   flex-direction: row;
   align-items: center;
   justify-content: space-around;
   box-sizing: border-box;
}

.nav-links {
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: space-around;
    padding: 10px;
}

.nav-links button { 
    border: none; 
    background: transparent;
    width: 160px;
    height: 160px;
    border-radius: 9px;
    padding-top: 1rem;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 10px;
}

.nav-links button:hover {
    background: rgba(128,128,128, 0.2);
}

.nav-links button p {
    font-size: 0.8rem;
    margin: 0;
    /*background: orange;*/
}

.nav-links button img {
    user-select: none;
    background-repeat: no-repeat;
    display: inline-block;
    fill: currentColor;
    height: 90px;
    width: 90px;
    overflow: hidden;
    filter: drop-shadow(0 1px 2px rgba(0, 0, 0, 0.5));
}

