async function asyncHtml() {
    let eqName = 'Variables del Equipo';

    var div = `<div class='container'>
                <div fxLayout="column" fxLayoutAlign="start stretch" fxLayoutGap=".5rem">
                    <div fxLayout="row" fxLayoutAlign="space-between center" fxLayoutGap="1rem" style="margin-bottom:0;">
                        <div fxLayout="row" fxLayoutAlign="start center" fxLayoutGap="1rem" style="margin-bottom:0; flex:1;">
                            <h7>${data[0]['entityName'] ? eqName : 'Equipo'}</h7>
                        </div>
                    </div>
                    <mat-divider style="margin-top: 0px; margin-bottom: 10px;"></mat-divider>
               `;

    var columns = [[], [], [], []]; // Arreglo para almacenar elementos de las cuatro columnas
    var columnIndex = 0; // Índice de la columna actual

    // Dividir los elementos en cuatro columnas
    for (var i = 0; i < data[0]['$datasource'].dataKeys.length; i++) {
        let keyName = data[0]['$datasource'].dataKeys[i].name;
        let label = humanize(keyName.toString());
        let value = data[0][keyName];

        if (value) { // Verificar si el valor no es vacío o nulo
            let paragraph = `<p><b>${label}:</b> ${value}</p>`;
            columns[columnIndex].push(paragraph);
            columnIndex = (columnIndex + 1) % 4; // Cambiar a la siguiente columna
        }
    }

    // Agregar columnas al contenedor principal
    div += `<div fxLayout="row" fxLayoutGap="1rem">
                <div style="flex:1;">${columns[0].join('')}</div>
                <div style="flex:1;">${columns[1].join('')}</div>
                <div style="flex:1;">${columns[2].join('')}</div>
                <div style="flex:1;">${columns[3].join('')}</div>
            </div>`;

    div += `</div></div>`;

    return div;
}






function humanize(str) {
  return str
    .replace(/^[\s_]+|[\s_]+$/g, '') // Elimina espacios al principio y al final
    .replace(/[_\s]+/g, ' ') // Reemplaza espacios y guiones bajos con un espacio
    .replace(/([a-z])([A-Z])/g, '$1 $2') // Agrega espacio entre minúsculas y mayúsculas
    .replace(/([A-Z]+)([A-Z][a-z])/g, '$1 $2') // Agrega espacio entre siglas (ej. RMS)
    .replace(/^[a-z]/, function(m) { return m.toUpperCase(); }); // Capitaliza la primera letra
}

function isJson(text) {
    try {
        JSON.parse(text);
    } catch (err) {
        return false;
    }
    return true;
}

